  const [downloadUrl, setDownloadUrl] = useState('');
  const [exportFormat, setExportFormat] = useState('');
  const [inputErrors, setInputErrors] = useState({
    email: false,
    category: false,
    keyword: false,
    period: false,
  });
  const socketRef = useRef(null);

  // WebSocketÊé•Á∂ö
